  useEffect(() => { 
    fetch(fet1 + sy1 + sy + sm1 + sm + sd1 + sd + ey1 + ey + em1 + em + ed1 + ed, {
         method: 'GET'
    })
    .then(res => res.json())  
    .then((result) => {
          result.map((item) => { 
  
            colorOptions1.push({
            label: item.label,
            value: item.value
          })
            
          })
          setColorOptions1(colorOptions1)
          //console.log(colorOptions)
        },
          (error) => {
            //this.setState({ error });
          }
        )
  })
  const colorOptions11 = JSON.parse(sessionStorage.getItem('facebook_campaign_sel'))

  const [colorOptions2, setColorOptions2] = useState([])
  const fett2 = 'https://srvr1px.cyberads.io/facebook_region/?brand_name='
  const fet2 = fett2 + brand_name
  useEffect(() => { 
    fetch(fet2 + sy1 + sy + sm1 + sm + sd1 + sd + ey1 + ey + em1 + em + ed1 + ed, {
         method: 'GET'
    })
    .then(res => res.json())  
    .then((result) => {
          result.map((item) => { 
  
            colorOptions2.push({
            label: item.label,
            value: item.value
          })
            
          })
          setColorOptions2(colorOptions2)
          //console.log(colorOptions)
        },
          (error) => {
            //this.setState({ error });
          }
        )
  })
  const colorOptions22 = JSON.parse(sessionStorage.getItem('facebook_region_sel'))

  const [colorOptions3, setColorOptions3] = useState([])
  const fett3 = 'https://srvr1px.cyberads.io/facebook_device/?brand_name='
  const fet3 = fett3 + brand_name
  useEffect(() => { 
    fetch(fet3 + sy1 + sy + sm1 + sm + sd1 + sd + ey1 + ey + em1 + em + ed1 + ed, {
