  //   })
    
  //   if (valReg.length === 0) {
     
  //     sessionStorage.setItem("fb_platform", 0)
  // }
  // }
  const handleOnChange1 = event => {
    const selval1 = []
    event.map(res => {
      const val1 = {}
      val1["value"] = res.value.replace("&", "@@@")
      val1["label"] = res.value.replace("&", "@@@")
      selval1.push(val1)

    })
    sessionStorage.setItem("facebook_campaign_sel", JSON.stringify(selval1))

  }
  // const handleOnChange1 = event => {
  //   const valReg = []
  //   event.map(res => {
  //     valReg.push(res.value)
      
  //     sessionStorage.setItem("fb_campaign", JSON.stringify(valReg))
      

  //   })
    
  //   if (valReg.length === 0) {
     
  //     sessionStorage.setItem("fb_campaign", 0)
  // }
  // }
  const handleOnChange2 = event => {
    const selval1 = []
    event.map(res => {
      const val1 = {}
