          result.map((item) => { 
  
            colorOptions.push({
            label: item.label,
            value: item.value
          })
            
          })
          setColorOptions(colorOptions)
          //console.log(colorOptions)
        },
          (error) => {
            //this.setState({ error });
          }
        )
  })
  const colorOptions11 = JSON.parse(sessionStorage.getItem('overall_campaign'))

  const [colorOptions2, setColorOptions2] = useState([])
