            label: item.label,
            value: item.value
          })
            
          })
          setColorOptions2(colorOptions2)
          //console.log(colorOptions)
        },
          (error) => {
            //this.setState({ error });
          }
        )
  })
  const colorOptions22 = JSON.parse(sessionStorage.getItem('overall_traffic'))

  
    const [colorOptions3, setColorOptions3] = useState([])
