import React, { useState } from "react"
import { Button, Col, Row } from "reactstrap"
import CustomDropDown from "../dashboard/ecommerce/CustomDropDown"
import Select, { components } from 'react-select'
import { selectThemeColors } from '@utils'
import { toast, ToastContainer } from "react-toastify"

export default function index() {
  const usersData = [
    {
      id:"1",
      name:"Sahil"
    }
  ]
  const PermissionData = [
    {
      id:"1",
      label:"dashboard",
      name:"dashboard"
    },
    {
      id:"2",
      label:"dashboard-2",
      name:"dashboard-2"
    },
    {
      id:"3",
      label:"dashboard-3",
      name:"dashboard-3"
    }
  ]
  const [Users, setUsers] = useState(usersData)
  const [Permission, setPermisson] = useState(PermissionData)
  const [selectedUsers, setSelectedUsers] = useState(0)
  const [selectedPermission, setSelectedPermission] = useState([])
  const handleUsers = async (e) => {
    setSelectedUsers(e.target.value)
  }
  const handlePermission = async (e) => {
    setSelectedPermission(e)
  } 
  const handleClick = async () => {
    const data = {
      user_id:selectedUsers,
      Permission:selectedPermission
    }
    toast.success("Uploaded")
  } 
  return (
    <>
    <ToastContainer />
      <Row>
      <select className="react-select form-control" value={''} onChange={handleUsers} required>
      <option value={''}>Choose Users</option>
      {Users.map((id) => {
        return (
          <>
            <option value={id.id}>{id.name}</option>
          </>
        )
      })}
      </select>
      
      {(selectedUsers > 0) && 
        <Select
          isClearable={false}
          theme={selectThemeColors}
          isMulti
          name='colors'
          options={Permission}
          // defaultValue={colorOptions00}
          className='react-select'
          onChange={handlePermission}
          placeholder='Select permission'
          classNamePrefix='select'
        />
      }

      <button onClick={handleClick} className="btn btn-primary">Submit </button>
     
        <Row className="laptophideCustomdropdown match-height mt-0 mb-1">
          <Col className="widthdate" xl="12" md="12" xs="12">
            {/* Select User  */}
            <Row>
          {/* <Col className='' md='12' sm='12'> */}
          {/* <h6 className='font-weight-bold'>Product</h6> */}
           
          {/* </Col> */}
        </Row>
          </Col>
        </Row>
      </Row>
    </>
  )
}
