import { useContext } from 'react'
import { List } from 'react-feather'
import { kFormatter } from '@utils'
import Avatar from '@components/avatar'
import Timeline from '@components/timeline'
import AvatarGroup from '@components/avatar-group'
import jsonImg from '@src/assets/images/icons/json.png'
import InvoiceList from '@src/views/apps/invoice/list'
import ceo from '@src/assets/images/portrait/small/avatar-s-9.jpg'
import { ThemeColors } from '@src/utility/context/ThemeColors'
import Sales from '@src/views/ui-elements/cards/analytics/Sales'
import AvgSessions from '@src/views/ui-elements/cards/analytics/AvgSessions'
import CardAppDesign from '@src/views/ui-elements/cards/advance/CardAppDesign'
import SupportTracker from '@src/views/ui-elements/cards/analytics/SupportTracker'
import { Row, Col, Card, CardHeader, CardTitle, CardBody, Media } from 'reactstrap'
import OrdersReceived from '@src/views/ui-elements/cards/statistics/OrdersReceived'
import CardCongratulations from '@src/views/ui-elements/cards/advance/CardCongratulations'
import SubscribersGained from '@src/views/ui-elements/cards/statistics/SubscribersGained'

import * as React from 'react'
import * as ReactDOM from 'react-dom'
import { NumericTextBox } from '@progress/kendo-react-inputs'
import { Chart, ChartTitle, ChartLegend, ChartTooltip, ChartSeries, ChartSeriesItem, ChartSeriesLabels } from '@progress/kendo-react-charts'
import { array } from 'prop-types'
// import data from './funnel-data.json';

const tooltipRender = props => {
  if (props.point) {
    return props.point.dataItem.stat
  }
}
const ChartContainer = () => {
  const [neckRation, setNeckRation] = React.useState(0.3)

  const data = [
    {stat: "Impressions"},
    {count: 434823},
    {color: "#0e5a7e"},
    {stat: "Clicks",count: 356854,color: "#166f99"}]
  return <div>
      <div className="example-config">
        <label htmlFor="neckRatio">Neck ratio ({neckRation})
          <br/>
          <NumericTextBox onChange={event => setNeckRation(event.value || 0)} value={neckRation} spinners={true} min={0} max={5} step={0.1}/>
        </label>
      </div>
      <Chart style={{
      margin: '0 auto',
      width: 300
    }}>
        <ChartTitle text="Sales funnel"/>
        <ChartSeries>
          <ChartSeriesItem type="funnel" data={data} categoryField="stat" field="count" colorField="color" neckRatio={neckRation}>
            <ChartSeriesLabels color="white" background="none" format="N0" />
          </ChartSeriesItem>
        </ChartSeries>
        <ChartTooltip render={tooltipRender}/>
        <ChartLegend visible={false} />
      </Chart>
    </div>
}
// ReactDOM.render(<ChartContainer />, document.querySelector('my-app'));

// const AnalyticsDashboard = () => { 
//     return (
//         <>heloo</>
//     )
//   }
  
export default ChartContainer
