import React, { useEffect, useState } from 'react'
import { IoIosArrowRoundForward, IoIosArrowForward } from 'react-icons/io'
import PerfectScrollbar from 'react-perfect-scrollbar'
import './Funnel.css'
import $ from 'jquery'
const MultipleValue = (res) => {
    // console.log(res)
    return (
        <>
        { Object.keys(res).map((val) => { 
            console.log(val)
            return (
            <div className='d-flex align-items-center p-0 m-0'>
                <IoIosArrowRoundForward size={32} className='text-warning' />
                {/* { (typeof res[val] === 'object') ? MultipleValue(res[val]) : <p className='fw-bold m-0'>{ res[val] }</p> }  */}

                        {/* <p className='fw-bold text-warning m-0'>{res[val]}-</p> */}
                    {/* </div> */}
                    {/* <div className='ml-3' /> */}
                    {/* { (typeof res[val] === 'object')  : <p className='fw-bold m-0'>{ res[val] }</p> }  */}
                        {/* <p><b>{res?.Tofu?.Reach?.toLocaleString()}</b></p> */}
            </div>
            ) 
        })}
        </>
    )
}
const Funnel = ({ res }) => {
    // $(document).ready(function () {
    //     $('.hide1').show()
    //     $('.hide2, .hide3, .hide4').hide()
    //     $('.rectangle01').click(function () {
    //         $('.hide2, .hide3, .hide4').hide()
    //         $('.hide1').show()
    //     })
    //     $('.rectangle03').click(function () {
    //         $('.hide1, .hide3, .hide4').hide()
    //         $('.hide2').show()
    //     })
    //     $('.rectangle05').click(function () {
    //         $('.hide1, .hide2, .hide4').hide()
    //         $('.hide3').show()
    //     })
    //     $('.rectangle07').click(function () {
    //         $('.hide1, .hide2, .hide3').hide()
    //         $('.hide4').show()
    //     })
    // })
    const [Key, setKey] = useState("")
    const storing = (str) => {
        return str.charAt(0).toUpperCase() + str.slice(1).toLowerCase()
    }
    const handleChange = (e) => {
        setKey(storing(e.target.textContent))    
    }
    useEffect(() => {
      setKey(Object.keys(res)[0])
    }, [])

    return (
        <>
            {res ? <div className="d-flex row rounded shadow">
                <div className="d-flex justify-content-center align-items-center flex-column col-lg-5 col-sm-12 p-0 py-1">
                    <div onClick={handleChange} className="taper rectangle01" data-toggle="tooltip" data-placement="top" title="Total Number Of Users" style={{ borderColor: '#FFC313 transparent', width: '360px' }}>
                    <p className='funnelPara text-center text-white'>TOFU</p></div>
                    <div className="rectangle02 h-none" style={{ width: 290 }} />
                    <div onClick={handleChange} className="taper rectangle03" data-toggle="tooltip" data-placement="top" title="User Engagement Behaviour (Number Of Users)" style={{ borderColor: '#4076DA transparent', width: '300px' }}><p className='funnelPara text-center text-white'>MOFU</p></div>
                    <div className="rectangle04 h-none" style={{ width: 235 }} />
                    <div onClick={handleChange} className="taper rectangle05" style={{ borderColor: '#4acade transparent', width: '240px' }}><p className='funnelPara text-center text-white'>BOFU</p></div>
                    <div className="rectangle06 h-none" style={{ width: 175 }} />
                    <div onClick={handleChange} className="taper rectangle07" style={{ borderColor: '#27CA75 transparent', width: '180px' }}><p className='funnelPara text-center text-white'>Conversion</p></div>
                </div>
                <Details res={res[Key]} />
                   
            </div> : ndivl}
        </>
    )
}
export default Funnel


const Details = ({res}) => {
    return (
        <>
        {res && Object.keys(res).length > 0 &&
            <div className="d-flex flex-row col-lg-7 col-sm-12 p-1">
                <div className="content-FFC313 hide1 w-100" style={{ height: 300 }}>
                    <div className="d-none d-lg-block w-25 position-relative border-top-warning" style={{ marginLeft: '-182px', top: 25, height: 0, zIndex: '-1000' }} />
                        <PerfectScrollbar className="w-100 border-left-warning">
                    <div className='d-flex align-items-center bg-warning mb-1'>
                        <p className='fw-bold text-white m-0' style={{ padding: 5 }}>Top-of-Funnel </p>
                    </div>
                    <div className='d-block' data-toggle="tooltip" data-placement="top" title="Total Number Of People Reached" >
                        {res && Object.keys(res).map((val, index) => {
                            return (
                                <>
                                <div className='d-flex align-items-center p-0 m-0'>
                                    <IoIosArrowRoundForward size={32} className='text-warning' />
                                            <p className='fw-bold text-warning m-0'>{val}-</p>
                                        </div>
                                        <div className='ml-3'>

                                        { (typeof res[val] === 'object') ? MultipleValue(res[val]) : <p className='fw-bold m-0'>{ res[val] }</p> } 
                                         {/* <p><b>{res?.Tofu?.Reach?.toLocaleString()}</b></p> */}
                                </div>
                                </>
                            )
                        })}
                    </div>
                        </PerfectScrollbar>
                    </div>
            </div>
        }
        </>
    )
}
