import * as React from 'react'
import * as ReactDOM from 'react-dom'
import { NumericTextBox } from '@progress/kendo-react-inputs'
import { Chart, ChartTitle, ChartLegend, ChartTooltip, ChartArea, ChartSeries, ChartSeriesItem, ChartSeriesLabels} from '@progress/kendo-react-charts'
import "hammerjs"
import { array } from 'prop-types'
// import data from './funnel-data.json';


const tooltipRender = (props) => {
    if (props.point) {
      return props.point.dataItem.stat
    }
}

const MOFUnav = (props) => {
    console.log('click event occure')
}

const data = [
    {
    stat: "TOFU",
    count: 923454,
    color: "#0e5a7e"
    },
    {
    stat: "MOFU",
    count: 523448,
    color: "#166f99"
    },
    {
    stat: "BOFU",
    count: 200438,
    color: "#2185b4"
    }
]
const ChartContainer1 = () => (
    <Chart
      style={
        {
        margin: "0 auto",
        // height : 100,
        width: 500
    }}>
    <ChartArea  height={500} />
    <ChartTitle text="Sales funnel" />
        <ChartSeries >
            <ChartSeriesItem
                type="funnel"
                data={data}
                categoryField="stat"
                field="count"
                colorField="color"
                dynamicSlope="50"
                dynamicHeight="200"  
                // seriesClick = {MOFUnav}         
                // seriesClick = {MOFUnav}
                // legendItemClick = {MOFUnav(e)}
                // target ={MOFUnav}
                // onPlotAreaClick={() => MOFUnav()}
                // nativeEvent={MOFUnav} 
                // target = {MOFUnav} 
                >
                <ChartSeriesLabels color="white" background="none" format="N0" />
            </ChartSeriesItem>

        </ChartSeries>
    <ChartTooltip render={tooltipRender} />
    <ChartLegend visible={false} />
    </Chart>
)
export default ChartContainer1
