                  </Col>
                  <Col>
                    <Label className="mt-1" for="endDate">
                      End Date
                    </Label>
                    <Input
                      id="endDate"
                      value={values.endDate}
                      name="endDate"
                      placeholder="date placeholder"
                      type="date"
                      onChange={handleChange}
                    />
                  </Col>
                </Row>
                <Label className="mt-1" for="frequencyTime">
                  Frequency Time
                </Label>
                <Select
                  defaultValue={values.Frequency}
                  onChange={e => {
                    setFieldValue("Frequency", e)
                  }}
                  options={option1}
                  name="Frequency"
                  placeholder="Frequency"
                />

                <Row>
                  <Col>
                    <Label className="mt-1" for="endDate">
                      Audience Type
                    </Label>
                    <Select
                      defaultValue={values.AudienceType}

                      onChange={e => {
                        setFieldValue("AudienceType", e)
                      }}
                      options={option}
                      name="AudienceType"
                      placeholder="Audience Type"
                    />
                  </Col>
                  <Col>
                    <Label className="mt-1" for="endDate">
                      Audience
                    </Label>
                    <Select
                      isMulti
                      defaultValue={values.Audience}
                      onChange={e => {
                        // console.log(e)
                        setFieldValue("Audience", e)
                      }}
                      options={option01}
                      name="Audience"
                      placeholder="Audience"
                    />
                  </Col>
                  {/* <Col>
                    <Label className="mt-1" for="endDate">
                      Select
                    </Label>
                    <Select
                        isMulti
                        onChange={e => {
                            // console.log(e)
                            setFieldValue("device", e)
                        }}
                        options={option02}
                        name="device"
                        placeholder="device"
                    />
                  </Col> */}
                </Row>
                <Row>
                  {values?.Audience && values?.Audience?.map((val) => {
                    return (
                      <>
                        {(val.label === "Age Group") &&
                          <Col>
                            <Label className="mt-1" for="endDate">
                              Age
                            </Label>
                            <Select
                              isMulti
                              defaultValue={editData.age}
                              onChange={e => {
                                // console.log(e)
                                setFieldValue("age", e)
                              }}
                              options={option03}
                              name="age"
                              placeholder="age"
                            />
                          </Col>}
                        {(val.label === "Gender") &&
                          <Col>
                            <Label className="mt-1" for="endDate">
                              Gender
                            </Label>
                            <Select
                              isMulti
                              defaultValue={editData.gender}
                              onChange={e => {
                                // console.log(e)
                                setFieldValue("gender", e)
                              }}
                              options={option04}
                              name="age"
                              placeholder="age"
                            />
                          </Col>}
                        {(val.label === "Device") &&
                          <Col>
                            <Label className="mt-1" for="endDate">
                              Device
                            </Label>
                            <Select isMulti
                              defaultValue={editData.device}
                              onChange={e => {
                                // console.log(e)
                                setFieldValue("device", e)
                              }}
                              options={option02}
                              name="device"
                              placeholder="device"
                            />
                          </Col>}
                        {(val.label === "OS") &&
                          <Col>
                            <Label className="mt-1" for="endDate">
                              Os
                            </Label>
                            <Select isMulti
                              defaultValue={editData.OS}
                              onChange={e => {
                                // console.log(e)
                                setFieldValue("OS", e)
                              }}
                              options={option05}
                              name="OS"
                              placeholder="OS"
                            />
                          </Col>}
                      </>
                    )
                  })}
                </Row>
                <Label className="mt-1" for="message">
                  Message
                </Label>
                <Input
                  id="message"
                  name="message"
                  placeholder="Message"
                  type="textarea"
                  value={values.message}
                  onChange={handleChange}
                  maxLength={260}
                />
                <Label className="mt-1" for="url">
                  URL
                </Label>
                <Input id="url" name="url" value={values.url} onChange={handleChange} placeholder="URL" type="url" />
              </FormGroup>
              <button type="submit" className="btn btn-primary align-center justify-content-center" disabled={isSubmitting}>
                Update
              </button>
              {/* <Button color="primary align-center justify-content-center">
                Save
              </Button> */}
            </Form>
          )}
        </Formik>
      </Modal>
    </div>
