  pending: 'light-warning',
  active: 'light-success',
  inactive: 'light-secondary'
}
const dat = [
  {
    name:"hello"
  },
   {
    name:"hello2"
  },
    
  {name:"hello3"
}
]
export const columns = [
  {
    name: 'Advertisers',
    minWidth: '120px',
    selector: 'email',
    sortable: true,
    cell: row => row.email
  },
  {
    name: 'User Name',
    minWidth: '120px',
    selector: 'email',
    sortable: true,
    cell: row => row.email
  },
  {
    name: 'Brand',
    minWidth: '120px',
    selector: 'brand_name',
    sortable: true,
    cell: row => row.brand_name
  },
  {
    name: 'User Type',
    minWidth: '120px',
    selector: 'user_type',
    sortable: true,
    cell: row => row.user_type
      // <Badge className='text-capitalize' color={statusObj[row.status]} pill>
      //   {row.status}
      // </Badge>
    
  },
  {
    name: 'Actions',
    minWidth: '75px',
    cell: row => (
      <UncontrolledDropdown>
        <DropdownToggle tag='div' className='btn btn-sm'>
          <MoreVertical size={18} className='cursor-pointer' />
        </DropdownToggle>
        <DropdownMenu right>

          <DropdownItem
            tag={Link}
            to={`/manage-user/user/edit/${row.id}`}
            className='w-100'
            onClick={() => store.dispatch(getUser(row.id))} >
            <FaUserEdit size={20} className='mr-50' />
            <span className='align-middle'>Edit User</span>
          </DropdownItem>

          <DropdownItem className='w-100' onClick={() => store.dispatch(deleteUser(row.id))}>
            <FaTrash size={16} className='mr-50' />
            <span className='align-middle'>Delete</span>
          </DropdownItem>
        </DropdownMenu>
      </UncontrolledDropdown>
    )
  }
]
